import update from 'immutability-helper'
import React, { useCallback, useEffect, useState } from 'react'
import { DnDCard } from './DnDCard.js'
import "./Favorites.css";
import FavoritesListDataService from '../services/favoriteslist'
import FavoritesDataService from '../services/favorites'

const style = {
  width: 500,
  margin: '1em',
}

export const Container = ({ user, favorites, waitRetrieval }) => {

  const [cards, setCards] = useState([]);
  const [deleteIndex, setDeleteIndex] = useState(null);
  const [insertIndex, setInsertIndex] = useState(null);
  console.log(user);

  const retrieveFavoriteMovies = useCallback(() => {
    if (user && waitRetrieval) {
      FavoritesListDataService.getFavoriteMovies(user.googleId)
        .then(res => {
          console.log(`in retrieve fav movies ${res.data}`);
          setCards(res.data);
        })
        .catch(e => {
          console.log(e);
        })
    }
  }, [user, waitRetrieval]);

  const moveCard = useCallback((dragIndex, hoverIndex) => {
      setCards((prevCards) =>
        update(prevCards, {
          $splice: [
            [dragIndex, 1],
            [hoverIndex, 0, prevCards[dragIndex]],
          ],
        }),
      )
      setDeleteIndex(dragIndex);
      setInsertIndex(hoverIndex);
    //  console.log(favorites)
    //  let temp = favorites.splice(dragIndex, 1)[0]
    //  favorites.splice(hoverIndex, 0, temp)
    //  updateFavoritesSort();
  })

  const reorderFavorites = (user, favorites, deleteIndex, insertIndex) => {
    console.log(`in reorderfavorites`);
    console.log(favorites)
    let temp = favorites.splice(deleteIndex, 1)[0]
    favorites.splice(insertIndex, 0, temp)
    updateFavoritesSort(user, favorites);
  }

  const updateFavoritesSort = (user, favorites) => {
    if (user) {
      var data = {
        _id: user.googleId,
        favorites: favorites
      }
        FavoritesDataService.updateFavorites(data)
          .then(res => {
            console.log(res.data);
            console.log('successfully updated')
          })
          .catch(e => {
            console.log(e);
          })
        }
  }

  useEffect(() => {
    if (user && waitRetrieval) {
      retrieveFavoriteMovies();
    }
  }, [user, waitRetrieval]);

  useEffect(() => {
    console.log(deleteIndex);
    if (waitRetrieval & deleteIndex & insertIndex) {
      console.log(favorites)
      reorderFavorites(user, favorites);
    }
  }, [user, favorites, waitRetrieval, deleteIndex, insertIndex])

  const renderCard = useCallback((card, index) => {
    return (
      <DnDCard
        key={card.id}
        index={index}
        id={card.id}
        text={card.text}
        poster={card.poster}
        moveCard={moveCard}
      />
    )
  }, [])
  return (
    <>
      <div style={style}>
        {cards.map((card, i) => renderCard(card, i))}
      </div>
    </>
  )
}
