import update from 'immutability-helper'
import React, { useCallback, useEffect, useState, useRef } from 'react'
import { DnDCard } from './DnDCard.js'
import "./Favorites.css";
import FavoritesListDataService from '../services/favoriteslist'
import FavoritesDataService from '../services/favorites'

const style = {
  width: 500,
  margin: '1em',
}

export const Container = ({ user, favorites, waitRetrieval }) => {

    const [cards, setCards] = useState([])
    const [doSaveFaves, setDoSaveFaves] = useState(false);

    const retrieveFavoriteMovies = useCallback(() => {
      if (user && waitRetrieval) {
        FavoritesListDataService.getFavoriteMovies(user.googleId)
          .then(res => {
            console.log(`in retrieve fav movies`,res.data);
            setCards(res.data);
            setDoSaveFaves(true);
          })
          .catch(e => {
            console.log(e);
          })
      }
    }, [user]);

    const moveCard = useCallback((dragIndex, hoverIndex) => {
      if (doSaveFaves) {
        setCards((prevCards) =>
          update(prevCards, {
            $splice: [
              [dragIndex, 1],
              [hoverIndex, 0, prevCards[dragIndex]],
            ],
          }),
        )
        console.log(favorites)
        let temp = favorites.splice(dragIndex, 1)[0]
        favorites.splice(hoverIndex, 0, temp)
        updateFavoritesSort();
      }
    }, [])

    const updateFavoritesSort = useCallback(() => {
      if (user && doSaveFaves) {
        var data = {
          _id: user.googleId,
          favorites: favorites
        }
          FavoritesDataService.updateFavorites(data)
            .then(res => {
              console.log(res.data);
              console.log('successfully updated')
              setDoSaveFaves(false);
            })
            .catch(e => {
              console.log(e);
            })
      }
    })

    useEffect(() => {
      retrieveFavoriteMovies();
    }, [user, favorites]);

    const renderCard = useCallback((card, index) => {
      console.log(card)
      return (
        <DnDCard
          key={card.id}
          index={index}
          id={card.id}
          text={card.text}
          poster={card.poster}
          moveCard={moveCard}
        />
      )
    }, [])
    return (
      <>
        <div style={style}>
          {cards.map((card, i) => renderCard(card, i))}
        </div>
      </>
    )
}
