import update from 'immutability-helper'
import React, { useCallback, useState } from 'react'
import { Card } from './Card.js'
import "./Favorites.css";
import FavoritesListDataService from '../services/favoriteslist'

export const Container = ({ user }) => {
    const [cards, setCards] = useState([])

    const retrieveFavoriteMovies = useCallback(() => {
      FavoritesListDataService.getFavoriteMovies(user.googleId)
        .then(res => {
          setCards(res.data);
        })
        .catch(e => {
          console.log(e);
        })
    }, [user]);

    const moveCard = useCallback((dragIndex, hoverIndex) => {
      setCards((prevCards) =>
        update(prevCards, {
          $splice: [
            [dragIndex, 1],
            [hoverIndex, 0, prevCards[dragIndex]],
          ],
        }),
      )
    }, [])
    const renderCard = useCallback((card, index) => {
      return (
        <Card
          key={card.id}
          index={index}
          id={card.id}
          text={card.text}
          moveCard={moveCard}
        />
      )
    }, [])
    return (
      <>
        <div className="favoritesPanel">{cards.map((card, i) => renderCard(card, i))}</div>
      </>
    )
}
