import update from 'immutability-helper'
import React, { memo, useRef, useCallback, useEffect, useState } from 'react'
import { Card } from './Card.js'
import "./Favorites.css";
import FavoritesListDataService from '../services/favoriteslist'
import FavoritesDataService from '../services/favorites'

export const Container = ({ user, favorites}) => {

    const [cards, setCards] = useState([])
    const firstUpdate = useRef(true);
    const retrieveFavoriteMovies = useCallback(() => {
      console.log(favorites)
      FavoritesListDataService.getFavoriteMovies(user.googleId)
        .then(res => {
          setCards(res.data);
          if (firstUpdate.current) {
            firstUpdate.current = false;
          }
          console.log(firstUpdate.current)
          console.log(user)
        })
        .catch(e => {
          console.log(e);
        })
    }, [user]);

    const moveCard = useCallback((dragIndex, hoverIndex) => {
      setCards((prevCards) =>
        update(prevCards, {
          $splice: [
            [dragIndex, 1],
            [hoverIndex, 0, prevCards[dragIndex]],
          ],
        }),
      )
      console.log(favorites)
      let temp = favorites.splice(dragIndex, 1)[0]
      favorites.splice(hoverIndex, 0, temp)
      updateFavoritesSort();
    }, [])

    const updateFavoritesSort = useCallback(() => {
      var data = {
        _id: user.googleId,
        favorites: favorites
      }
      FavoritesDataService.updateFavorites(data)
        .then(res => {
          console.log(res.data);
          console.log('successfully updated')
        })
        .catch(e => {
          console.log(e);
        })
    }, [])

    const getInitialStateUser = useCallback(() => {
      var selectedUser = localStorage.getItem('SelectedUser') || null;

      return {
        user: selectedUser
      };
    })

    useEffect(() => {
      if (user) {
        updateFavoritesSort();
      }
    }, [favorites])

    useEffect(() => {
      retrieveFavoriteMovies();
      localStorage.setItem('SelectedUser', user);
      localStorage.setItem('SelectedFavorites', favorites)
    }, []);

    const renderCard = useCallback((card, index) => {
      return (
        <Card
          key={card.id}
          index={index}
          id={card.id}
          text={card.text}
          poster={card.poster}
          moveCard={moveCard}
        />
      )
    }, [])
    return (
      <>
        <div className="favoritesPanel">{cards.map((card, i) => renderCard(card, i))}</div>
      </>
    )
}
