  //  }
  //}

  useEffect(() => {
    if (favorites && deleteIndex !== null && insertIndex !== null) {
      reorderFavorites(favorites, deleteIndex, insertIndex);
    }
  }, [user, favorites, deleteIndex, insertIndex])

  const renderCard = useCallback((card, index) => {
    return (
      <DnDCard
        key={card.id}
        index={index}
        id={card.id}
        text={card.text}
        poster={card.poster}
        moveCard={moveCard}
      />
    )
  }, [])
  return (
    <>
      <div style={style}>
        {cards.map((card, i) => renderCard(card, i))}
      </div>
    </>
  )
}
